import React, { useState, useEffect } from 'react';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import PrizeGrid from './components/PrizeGrid';
import FAQ from './components/FAQ';
import Footer from './components/Footer';
import HowItWorks from './components/HowItWorks';
import WinningNumber from './components/WinningNumber';
import Registration from './components/Registration';


export type TabType = 'home' | 'how-it-works' | 'winning-number' | 'register';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState<TabType>('home');
  const [isDark, setIsDark] = useState(true);

  useEffect(() => {
    if (isDark) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [isDark]);

  const toggleTheme = () => setIsDark(!isDark);

  return (
    <div className="min-h-screen flex flex-col overflow-x-hidden bg-custom-bg text-custom-text transition-colors duration-300">
      <Navbar 
        onNavigate={setActiveTab} 
        activeTab={activeTab} 
        isDark={isDark} 
        onToggleTheme={toggleTheme} 
      />

      <main className="flex-grow pt-20">
        {activeTab === 'home' && (
          <>
            {/* 2. Pass onNavigate so buttons inside these components can switch tabs */}
            <Hero onNavigate={setActiveTab} />
            <PrizeGrid onNavigate={setActiveTab} />
            <FAQ />
          </>
        )}

        {/* 3. Pass onNavigate here too for the bottom CTA button */}
        {activeTab === 'how-it-works' && <HowItWorks onNavigate={setActiveTab} />}

        {activeTab === 'winning-number' && <WinningNumber />}

        {/* 4. Registration now has its own exclusive view */}
        {activeTab === 'register' && <Registration />}
      </main>

      <Footer onNavigate={setActiveTab} />
    </div>
  );
};

export default App;
