import React, { useState } from 'react';
import { Search, CheckCircle2, AlertCircle, Calendar, History, Loader2, Database } from 'lucide-react';

interface RecentWinner {
  eventName: string;
  number: string; // Range 1 - 100
  stakeAmount: string;
  eventDate: string;
}

const RECENT_WINNERS: RecentWinner[] = [
  { eventName: 'Mega Jackpot', number: '42', stakeAmount: '₦5,000', eventDate: '14/05/2024' },
  { eventName: 'Daily Rush', number: '12', stakeAmount: '₦500', eventDate: '14/05/2024' },
  { eventName: 'Nicket Special', number: '98', stakeAmount: '₦2,000', eventDate: '12/05/2024' },
  { eventName: 'Gadget Draw', number: '7', stakeAmount: '₦1,000', eventDate: '10/05/2024' },
  { eventName: 'Quick Win', number: '63', stakeAmount: '₦200', eventDate: '11/05/2024' },
];

const WinningNumber: React.FC = () => {
  const [ticketId, setTicketId] = useState('');
  const [status, setStatus] = useState<'idle' | 'won' | 'lost' | 'not_found' | 'searching'>('idle');

  // Simulated backend check for payment transaction
  const verifyWithBackend = async (id: string) => {
    setStatus('searching');

    // Simulate real database network delay
    await new Promise(resolve => setTimeout(resolve, 2000));

    // Simulation logic:
    // "TK-WIN-01" triggers a win
    // "TK-MISS-01" triggers a loss
    // Anything else is treated as a loss for the demo
    const cleanId = id.toUpperCase();
    if (cleanId.includes('WIN')) {
      setStatus('won');
    } else if (cleanId.includes('MISS')) {
      setStatus('lost');
    } else if (cleanId.length < 5) {
       setStatus('not_found');
    } else {
      setStatus('lost');
    }
  };

  const handleCheck = (e: React.FormEvent) => {
    e.preventDefault();
    if (!ticketId) return;
    verifyWithBackend(ticketId);
  };

  return (
    <div className="max-w-7xl mx-auto px-6 py-32 min-h-[80vh] flex flex-col items-center">
      <div className="text-center mb-16">
        <h1 className="text-5xl md:text-7xl font-albert font-black mb-8">
          CHECK YOUR <span className="text-brand-light dark:text-brand-dark italic">LUCK</span>
        </h1>
        <p className="text-xl text-gray-500 dark:text-gray-400 max-w-2xl mx-auto">
          We'll search our database for your payment transaction to see if you matched the winning number (1-100).
        </p>
      </div>

      <div className="w-full max-w-2xl bg-custom-card p-8 md:p-12 rounded-[40px] border border-custom shadow-2xl mb-20 relative overflow-hidden">
        {/* Background Glow */}
        <div className="absolute -top-24 -left-24 w-64 h-64 bg-brand-light/10 dark:bg-brand-dark/10 blur-[100px] rounded-full"></div>

        <form onSubmit={handleCheck} className="space-y-8 relative z-10">
          <div>
            <div className="flex items-center gap-2 mb-4">
              <Database size={14} className="text-gray-400" />
              <label className="block text-xs font-black uppercase tracking-[0.2em] text-gray-400">
                Ticket ID / Transaction Reference
              </label>
            </div>
            <div className="relative group">
              <input 
                type="text" 
                placeholder="e.g. TK-8892-WIN"
                value={ticketId}
                onChange={(e) => setTicketId(e.target.value)}
                className="w-full bg-gray-50 dark:bg-black/40 border-2 border-custom rounded-2xl px-6 py-5 text-xl font-bold focus:border-brand-light dark:focus:border-brand-dark outline-none transition-all placeholder:text-gray-400"
                required
              />
              <div className="absolute right-6 top-1/2 -translate-y-1/2 text-gray-400 group-focus-within:text-brand-light dark:group-focus-within:text-brand-dark transition-colors">
                {status === 'searching' ? <Loader2 size={24} className="animate-spin" /> : <Search size={24} />}
              </div>
            </div>
          </div>

          <button 
            type="submit"
            disabled={status === 'searching'}
            className="w-full bg-custom-btn py-5 rounded-2xl text-xl font-bold hover:scale-[1.01] active:scale-[0.98] transition-all disabled:opacity-50 disabled:cursor-not-allowed shadow-xl shadow-brand-light/20 dark:shadow-brand-dark/10 flex items-center justify-center gap-3"
          >
            {status === 'searching' ? 'Checking Database...' : 'Verify Ticket'}
          </button>
        </form>

        {/* Results Display - High-Quality Glassmorphic Popup */}
        <div className="mt-12 overflow-hidden transition-all duration-500 min-h-[160px] flex items-center justify-center">
          {status === 'won' && (
            <div className="w-full relative p-8 rounded-[32px] text-center animate-in zoom-in slide-in-from-bottom-4 duration-500 border border-white/20 dark:border-white/10 shadow-2xl backdrop-blur-2xl bg-green-500/20 dark:bg-green-500/10">
              <div className="absolute inset-0 bg-white/5 pointer-events-none"></div>
              <div className="relative z-10">
                <div className="flex justify-center mb-6">
                  <div className="p-4 bg-green-500 rounded-full shadow-lg shadow-green-500/50 scale-110">
                    <CheckCircle2 size={40} className="text-white" />
                  </div>
                </div>
                <h3 className="text-3xl font-black text-green-600 dark:text-green-400 mb-2">DATABASE MATCH FOUND!</h3>
                <p className="text-gray-600 dark:text-gray-300 font-medium mb-6">Congratulations! This transaction matched a winning number in our system.</p>
                <div className="p-4 bg-white/30 dark:bg-black/30 rounded-2xl border border-white/20 inline-block px-8 font-passion text-3xl tracking-wider">
                  WINNER: ₦50,000
                </div>
              </div>
            </div>
          )}

          {status === 'lost' && (
            <div className="w-full relative p-8 rounded-[32px] text-center animate-in zoom-in slide-in-from-bottom-4 duration-500 border border-white/20 dark:border-white/10 shadow-2xl backdrop-blur-2xl bg-gray-500/10 dark:bg-white/5">
              <div className="relative z-10">
                <div className="flex justify-center mb-6">
                  <div className="p-4 bg-gray-400 dark:bg-gray-600 rounded-full">
                    <AlertCircle size={40} className="text-white" />
                  </div>
                </div>
                <h3 className="text-3xl font-black text-gray-500 dark:text-gray-400 mb-2">NO DRAW MATCH</h3>
                <p className="text-gray-500 dark:text-gray-400">Transaction found, but the numbers didn't align this time. Don't give up!</p>
              </div>
            </div>
          )}

          {status === 'not_found' && (
            <div className="w-full relative p-8 rounded-[32px] text-center animate-in zoom-in slide-in-from-bottom-4 duration-500 border border-brand-light/20 shadow-2xl backdrop-blur-2xl bg-brand-light/10">
              <div className="relative z-10">
                <div className="flex justify-center mb-6">
                  <div className="p-4 bg-brand-light rounded-full">
                    <Search size={40} className="text-white" />
                  </div>
                </div>
                <h3 className="text-2xl font-black text-brand-light dark:text-brand-dark mb-2">TRANSACTION NOT FOUND</h3>
                <p className="text-gray-600 dark:text-gray-300">We couldn't find a payment associated with this Ticket ID. Please double-check your record.</p>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Last 5 Winning Numbers Table */}
      <div className="w-full max-w-4xl">
        <div className="flex items-center gap-3 mb-8">
          <div className="p-3 bg-brand-light/10 dark:bg-brand-dark/10 rounded-2xl">
            <History size={32} className="text-brand-light dark:text-brand-dark" />
          </div>
          <h2 className="text-3xl font-albert font-bold italic tracking-tight">Recent Winning Numbers (1-100)</h2>
        </div>

        <div className="bg-custom-card border border-custom rounded-[32px] overflow-hidden shadow-xl">
          <div className="overflow-x-auto">
            <table className="w-full text-left border-collapse">
              <thead>
                <tr className="bg-gray-50 dark:bg-white/5 text-gray-500 text-xs font-black uppercase tracking-[0.1em] border-b border-custom">
                  <th className="px-8 py-6">Event Name</th>
                  <th className="px-8 py-6">Winning #</th>
                  <th className="px-8 py-6">Stake</th>
                  <th className="px-8 py-6">Event Date</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-custom">
                {RECENT_WINNERS.map((winner, idx) => (
                  <tr key={idx} className="hover:bg-brand-light/[0.02] dark:hover:bg-brand-dark/[0.02] transition-colors group">
                    <td className="px-8 py-6">
                      <span className="font-albert font-bold text-lg">{winner.eventName}</span>
                    </td>
                    <td className="px-8 py-6">
                      <span className="px-6 py-2 bg-brand-light dark:bg-brand-dark text-white dark:text-black rounded-xl font-passion text-2xl tracking-widest inline-block shadow-md">
                        {winner.number}
                      </span>
                    </td>
                    <td className="px-8 py-6 text-gray-500 dark:text-gray-400 font-medium text-lg">
                      {winner.stakeAmount}
                    </td>
                    <td className="px-8 py-6">
                       <div className="flex items-center gap-2 text-gray-500 dark:text-gray-400">
                         <Calendar size={18} />
                         <span className="font-medium">{winner.eventDate}</span>
                       </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      <div className="mt-32 flex flex-wrap justify-center gap-12 text-center">
        <div className="flex flex-col items-center">
          <span className="text-4xl font-black text-brand-light dark:text-brand-dark">12.4k</span>
          <span className="text-sm font-bold text-gray-500 uppercase tracking-widest">Winners Today</span>
        </div>
        <div className="flex flex-col items-center">
          <span className="text-4xl font-black text-brand-light dark:text-brand-dark">₦2.5M</span>
          <span className="text-sm font-bold text-gray-500 uppercase tracking-widest">Paid Out Weekly</span>
        </div>
        <div className="flex flex-col items-center">
          <span className="text-4xl font-black text-brand-light dark:text-brand-dark">100%</span>
          <span className="text-sm font-bold text-gray-500 uppercase tracking-widest">Verifiable Draws</span>
        </div>
      </div>
    </div>
  );
};

export default WinningNumber;
